import Data.List

distinctN :: [Char] -> Int -> Bool
distinctN x n = length (nub x) == n

detectPacket :: [Char] -> Int -> Int
detectPacket [] _ = 0
detectPacket (x:y:z:w:xs) n
  | distinctN (x:y:z:w:[]) 4 = n + 4
  | otherwise = detectPacket (y:z:w:xs) (n+1)

detectMessage :: [Char] -> Int -> Int
detectMessage [] _ = 0
detectMessage (x1:x2:x3:x4:x5:x6:x7:x8:x9:x10:x11:x12:x13:x14:xs) n
  | distinctN (x1:x2:x3:x4:x5:x6:x7:x8:x9:x10:x11:x12:x13:x14:[]) 14 = n + 14
  | otherwise = detectMessage (x2:x3:x4:x5:x6:x7:x8:x9:x10:x11:x12:x13:x14:xs) (n+1)

